sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var utils = new EgnyteUtils();
	var oauth2Profile = utils.getOAuthProfileFromName(current.endpoint.toString());
	if (oauth2Profile == '') {
		gs.addErrorMessage('OAuth Profile not found. Please contact support.');
		return;
	}
	current.oauth2_profile = oauth2Profile;

	// there must be a single configuration, so deleting existing configuration while adding new one
	if(current.operation() == 'insert') {
		var gr = new GlideRecord('x_snc_egnyte_prote_egnyte_configuration');
		gr.query();
		gr.setValue('active', false);
		gr.updateMultiple();
		gr.deleteMultiple();

		// Update redirect URL for OAuth flow
		utils.updateOAuthProfileRedirects('Egnyte Protect US');
		utils.updateOAuthProfileRedirects('Egnyte Protect EU');	

		EgnyteLogger.info('New configuration added by '+ gs.getUserName() + '(' + gs.getUserID() + ')');
	} else {
		EgnyteLogger.info(gs.getUserName() + '(' + gs.getUserID() + ') updated configuration');
	}

})(current, previous);
