sys_script_include.script:
var EgnyteLogger = Class.create();
EgnyteLogger.prototype = {
    initialize: function() {
    },

	type: 'EgnyteLogger'
};

EgnyteLogger.log = function(msg, level) {
	var entry = new GlideRecordSecure('x_snc_egnyte_prote_egnyte_app_logs');
	entry.newRecord();
	entry.setValue('log_message', msg);
	entry.setValue('log_level', level);
	return entry.insert();	
};

EgnyteLogger.error = function(msg) {
	return EgnyteLogger.log(msg, 'ERROR'); 
};

EgnyteLogger.info = function(msg) {
	return EgnyteLogger.log(msg, 'INFO'); 
};

EgnyteLogger.debug = function(msg) {
	return EgnyteLogger.log(msg, 'DEBUG'); 
};
